 CREATE TABLE category(id INTEGER NOT NULL PRIMARY KEY AUTO_INCREMENT,title VARCHAR(255) NOT NULL,date TIMESTAMP NOT NULL);
 CREATE TABLE channel(id INTEGER NOT NULL PRIMARY KEY AUTO_INCREMENT,title VARCHAR(255) NOT NULL,link VARCHAR(255) NOT NULL,lastSynchronize TIMESTAMP NOT NULL,synchronize BOOLEAN NOT NULL);
 CREATE TABLE feed(id INTEGER NOT NULL PRIMARY KEY AUTO_INCREMENT,guid INTEGER NOT NULL, title VARCHAR(255) NOT NULL, pubDate TIMESTAMP NOT NULL,link VARCHAR(255) NOT NULL);